jQuery(function(){

  jQuery(".timeline .timeline-item:nth-child(odd) .timeline-content:not(:in-viewport)").addClass("js--fadeInLeft");
  jQuery(".timeline .timeline-item:nth-child(even) .timeline-content:not(:in-viewport)").addClass("js--fadeInRight");

  window.sr = ScrollReveal();

  if (jQuery(window).width() < 768) {

  	if (jQuery('.timeline-content').hasClass('js--fadeInLeft')) {
  		jQuery('.timeline-content').removeClass('js--fadeInLeft').addClass('js--fadeInRight');
  	}

  	sr.reveal('.js--fadeInRight', {
	    origin: 'right',
	    distance: '300px',
	    easing: 'ease-in-out',
	    duration: 800,
	  });

  } else {

  	sr.reveal('.js--fadeInLeft', {
	    origin: 'left',
	    distance: '300px',
		  easing: 'ease-in-out',
	    duration: 800,
	  });

	  sr.reveal('.js--fadeInRight', {
	    origin: 'right',
	    distance: '300px',
	    easing: 'ease-in-out',
	    duration: 800,
	  });

  }

  sr.reveal('.js--fadeInLeft', {
	    origin: 'left',
	    distance: '300px',
		  easing: 'ease-in-out',
	    duration: 800,
	  });

	  sr.reveal('.js--fadeInRight', {
	    origin: 'right',
	    distance: '300px',
	    easing: 'ease-in-out',
	    duration: 800,
	  });


});
